global.schema:
type AccountTransactionsState {
  account: Account!
  transactions: [TransactionEntry!]!
  budgets: [Budget!]!
}

type Account {
    id: OID!
    account: String!
    name: String!
    budgetPath: String
    accountTransactionsId: PHID
    chain: [String!]
    type: String
    owners: [String!]
    KycAmlStatus: String
}

type TransactionDetails {
    txHash: String!
    token: Currency!
    blockNumber: Int
    uniqueId: String
}

type TransactionEntry {
    id: ID!
    counterParty: EthereumAddress
    amount: Amount_Currency!
    datetime: DateTime!
    details: TransactionDetails!
    budget: OID
    accountingPeriod: String!
    direction: TransactionDirection!
}

type Budget {
    id: OID!
    name: OLabel
}

enum TransactionDirection {
    INFLOW
    OUTFLOW
}

enum TransactionDirectionInput {
    INFLOW
    OUTFLOW
}
