operations.schema:
input UpdateKycStatusInput {
    id: OID!
    KycAmlStatus: KycAmlStatusTypeInput!
}

global.schema:
type AccountsState {
   accounts: [AccountEntry!]!
}

type AccountEntry {
    id: OID!
    account: String!
    name: String!
    budgetPath: String
    accountTransactionsId: PHID
    chain: [String!]
    type: AccountType!
    owners: [String!]
    KycAmlStatus: KycAmlStatusType
}

enum AccountType {
    Source
    Internal
    Destination
    External
}

enum KycAmlStatusType {
    PASSED
    PENDING
    FAILED
}

enum AccountTypeInput {
  Source
  Internal
  Destination
  External
}

enum KycAmlStatusTypeInput {
  PASSED
  PENDING
  FAILED
}
