operations.schema:
input AddWalletInput {
  wallet: EthereumAddress!
  name: String
}

input AddBillingStatementInput {
  wallet: EthereumAddress!
  billingStatementId: OID!
}

input RemoveBillingStatementInput {
  wallet: EthereumAddress!
  billingStatementId: OID!
}

input RemoveLineItemInput {
  wallet: EthereumAddress!
  lineItemId: ID!
}

input AddLineItemGroupInput {
  id: ID!
  label: String
  parentId: ID
}

input UpdateLineItemGroupInput {
  id: ID!
  label: String
  parentId: ID
}

input SetGroupTotalsInput {
  wallet: EthereumAddress!
  groupTotals: GroupTotalsInput!
}

input GroupTotalsInput {
  group: ID!
  totalBudget: Float
  totalForecast: Float
  totalActuals: Float
  totalPayments: Float
}

input RemoveGroupTotalsInput {
  wallet: EthereumAddress!
  groupId: ID!
}

input UpdateWalletInput {
  address: EthereumAddress!
  name: String
  accountDocumentId: PHID
  accountTransactionsDocumentId: PHID
}
